# plots all of the melted averages with respect to year on one plot as points. Then plots a fitted curve line with a gray error width, as well as a dotted linear fitted line.
wr3 %>% ggplot(aes(x=YEAR, y=value, color=variable)) + geom_point(alpha=0.5) + stat_smooth(alpha=0.15) + scale_y_log10() + theme(panel.background = element_rect(fill = 'white'), panel.grid.major = element_line(colour = "#B2B2B2", size=0.25), panel.grid.minor = element_line(colour = "#B2B2B2", size=0.25)) + stat_smooth(method="lm", se=FALSE, linetype="dotted") + labs(x="Year", y = "log( Mean Values )") + theme(plot.title=element_text(size=20, face="bold", vjust=2, family="Helvetica Neue Light")) + ggtitle("New York Crime Averages Per Year") + scale_color_discrete(name="Crime Type")